import React, { useEffect, useState } from 'react'
import Admin_header from './Admin_header'
import axios from 'axios'

function SimpleViewPackage() {
    const [pack,setpack]=useState([])
    useEffect(()=>{
        fetchdata()
    },[])
const fetchdata=async()=>{
    const res = await axios.get("http://localhost:3000/packges")
    setpack(res.data)
}
const handledelete=async(id)=>{
    const res = await axios.delete(`http://localhost:3000/packges/${id}`)
    setpack(res.data)
    fetchdata()
}
  return (
    <div>
      <Admin_header heading={"Simple View all the packages.."}/>
          <table className="table table-dark">
              <thead>
                  <tr className='text-center'>
                      <th scope="col">ID</th>
                      <th scope="col">Country</th>
                      <th scope="col">Days</th>
                      <th scope="col">Person</th>
                      <th scope="col">Price</th>
                      <th scope="col">Image</th>
                      <th scope="col">Action</th>
                  </tr>
              </thead>
              <tbody>
                {
                    pack && pack.map((pack)=>{
                        return(
                            <tr className='text-center'>
                            <th scope="row">{pack.id}</th>
                            <td>{pack.coutry}</td>
                            <td>{pack.days}</td>
                            <td>{pack.person}</td>
                            <td>{pack.price}</td>
                            <td><img className="img-fluid" src={pack.url} style={{height:"90px", width:"60%"}} alt=''/></td>
                            <td>
                              <button className='btn btn-primary'>View</button>
                              <button className='btn btn-success mx-2' >Edit</button>
                              <button className='btn btn-danger' onClick={handledelete(pack.id)}>Delete</button>
                            </td>
                        </tr>
                        )

                    })
                }  
              </tbody>
          </table>


    </div>
  )
}

export default SimpleViewPackage
